import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import products from "../products";
import {addToCart, removeFromCart} from "../actions/cartActions";
import {Button, Card, Col, Form, Image, ListGroup, Row} from "react-bootstrap";
import Message from "../components/Message";
import {Link} from 'react-router-dom'

function CartScreen({match,location,history}) {
    const productId = match.params.id
    const qty = location.search ? Number(location.search.split('=')[1]) : 1
    const dispatch = useDispatch()
    useEffect(() => {
        if(productId){
            dispatch(addToCart(productId,qty))
        }

    }, [dispatch,productId,qty]);
    const removeFromCartHandler =(id)=>{
        dispatch(removeFromCart(id))
    }
    const ckeckoutHandler = ()=>{
        history.push('/login?redirect=shipping')
    }
    const cart = useSelector(state=>state.cart)
    const {cartItems} = cart
    console.log(cartItems)

    return (
        <Row>
            <Col md={8}>
                <h1>shopping cart</h1>
                {cartItems.length === 0 ? (
                    <Message variant='info'>cart is empty <Link to='/'>go back</Link></Message>
                ):(
                    <ListGroup variant='flush'>
                        {cartItems.map((item)=>(
                            <ListGroup.Item key={item.product}>
                                <Row>
                                    <Col md={2}>
                                        <Image src={item.image} alt={item.name} fluid rounded />
                                    </Col>
                                    <Col md={3}>
                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                    </Col>
                                    <Col md={2}>
                                        ${item.price}
                                    </Col>
                                    <Col md={3}>
                                        <Form.Control as='select' value={item.qty} onChange={(e) =>dispatch(addToCart(item.product,Number(e.target.value)))}>
                                            {
                                                [...Array(item.countInStock).keys()].map((x)=><option value={x+1} key={x+1}>{x+1}</option>)
                                            }



                                        </Form.Control>
                                    </Col>
                                    <Col md={1}>
                                        <Button type='button' variant='light' onClick={()=>removeFromCartHandler(item.product)}>
                                            <i className='fas fa-trash'></i>

                                        </Button>

                                    </Col>
                                </Row>

                            </ListGroup.Item>
                        ))}

                    </ListGroup>
                )
                }
            </Col>
            <Col md={4}>
                <Card>
                    <ListGroup variant='flush'>
                        <ListGroup.Item>
                            <h2>Subtotal ({cartItems.reduce((acc,item)=>acc+item.qty,0)}) items</h2>
                            $ ({cartItems.reduce((acc,item)=>acc+item.qty * item.price,0).toFixed(2)})
                        </ListGroup.Item>
                    </ListGroup>
                    <ListGroup.Item>
                        <Button type='button' className='btn-block' disabled={cartItems.length ===0} onClick={ckeckoutHandler}>
                            proceed checkout

                        </Button>
                    </ListGroup.Item>
                </Card>

            </Col>
        </Row>
    );
}

export default CartScreen;
